import React, {useEffect, useState, useRef, useMemo, Ref} from 'react';
import axios from "axios";
import {Table, Container, Modal, Button} from 'react-bootstrap';
import { MovieDto } from '../model';
import { AiFillEye, AiFillEdit, AiFillDelete } from 'react-icons/ai';
import InfiniteScroll from './InfiniteScroll';
import { BASE_API } from '../constants';

const MoviesTable: React.FC = () => {
    const [movies, setMovies] = useState<MovieDto[]>([])
    const [page, setPage] = useState<number>(0)
    const [size, setSize] = useState<number>(10)
    const [loading, setLoading] = useState(false);
    const [last, setLast] = useState(false)
    const [show, setShow] = useState(false);
    useState

    const hasMoreData = !last;

    const loadMoreNumbers = () => {
        setLoading(true);
            axios.get(BASE_API, {params: {"pageSize" : size, "pageNumber": page}}).then(res =>{
                setPage(page+1)
                setMovies([...movies, ...res.data.content]);
                setLoading(false);
              })
    };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
    <Container className='tableFixHead'>
        <InfiniteScroll
            hasMoreData={hasMoreData}
            isLoading={loading}
            onBottomHit={loadMoreNumbers}
            loadOnMount={true}
        >
            <Table>
                <thead>
                <tr>
                    <th>RANK</th>
                    <th>TITLE</th>
                    <th>YEAR</th>
                    <th>RATING</th>
                    <th>REVENUE</th>
                    <th></th>
                </tr>
                </thead>
                <tbody>
                {movies.map((movie, index) =>(
                    <tr key={index}>
                    <td>{index+1}</td>
                    <td>{movie.title}</td>
                    <td>{new Date(movie.date).getFullYear()}</td>
                    <td>{movie.rating}</td>
                    <td>${movie.revenue.toLocaleString('en-US', {maximumFractionDigits:2})}</td>
                    <td><AiFillEye size={25} onClick={handleShow}/> <AiFillEdit size={25}/> <AiFillDelete size={25}/></td>
                    </tr>
                )
                )}
                </tbody>
            </Table>
        </InfiniteScroll>
      </Container>

      <Modal show={show} onHide={handleClose}
        backdrop="static">
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      </>
  )
}

export default MoviesTable
