import React, { useState } from 'react'
import { Button, CloseButton, Container, DropdownButton } from 'react-bootstrap'
import DropdownItem from 'react-bootstrap/esm/DropdownItem';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {

    const navigate = useNavigate();
    const [year, setYear] = useState("per Year")

    const buildOptions: string[] = () => {
      var arr = [];

      for (var i = 2022; i > 1950; i--) {
          arr.push(i.toString)
      }

      return arr; 
  }

  return (
    <Container>
        <Container className='header'></Container>
        <Container className='navigation'>
            <h1>Movie ranking</h1>
            <Button variant="outline-secondary" onClick={() => navigate('/')}>All movies</Button>
            <Button variant="outline-secondary" onClick={() => navigate('topMovies')}>Top 10 Revenue</Button>
            <Button variant="outline-secondary" onClick={() => navigate('topMovies')}>Top 10 Revenue {year}</Button>
            <DropdownButton variant="outline-secondary" title={"Top 10 Revenue " + year}>
              {
                buildOptions.ma
              }
            </DropdownButton>
        </Container>
    </Container>
  )
}

export default Navbar
