import React, {useEffect, useState, useRef, useMemo, Ref} from 'react';
import axios from "axios";
import {Table, Container} from 'react-bootstrap';
import { MovieDto } from '../model';
import { keyboard } from '@testing-library/user-event/dist/keyboard';
import { Waypoint } from 'react-waypoint';

const MoviesTable: React.FC = () => {
    const [movies, setmovies] = useState<MovieDto[]>([])
    const [page, setpage] = useState<number>(0)
    const [size, setsize] = useState<number>(10)

    const URL: string = "http://localhost:8080/app/movies";

    const ref1 = useRef<Waypoint>(null);

    const isInViewport1 : boolean = useIsInViewport(ref1);
  
    const handleScroll = () =>{
        axios.get(URL, {params: {"pageSize" : size, "pageNumber": page}}).then(res =>{
            setpage(page+1)
            setmovies([...movies, ...res.data.content]);
          })
    }

    useEffect(() => {
        while (isInViewport1) {            
            axios.get(URL, {params: {"pageSize" : size, "pageNumber": page}}).then(res =>{
                setpage(page+1)
                setmovies([...movies, ...res.data.content]);
      })
        }
    }, [])

  return (
    <Container className='tableFixHead' onScroll={handleScroll}>
    <Table striped bordered hover variant='dark' >
        <thead>
          <tr>
            <th>Rank</th>
            <th>Title</th>
            <th>Date</th>
            <th>Rating</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie, index) =>(
            <tr key={index}>
              <th>{index+1}</th>
              <th>{movie.title}</th>
              <th>{movie.date}</th>
              <th>{movie.rating}</th>
              <th>{movie.revenue}</th>
            </tr>
          )
          )}
          <Waypoint onEnter={handleScroll} ref={ref1} />
        </tbody>
      </Table>
      </Container>
  )
}

function useIsInViewport(ref : any) {
    const [isIntersecting, setIsIntersecting] = useState<boolean>(false);
  
    const observer = useMemo(
      () =>
        new IntersectionObserver(([entry]) =>
          setIsIntersecting(entry.isIntersecting),
        ),
      [],
    );
  
    useEffect(() => {
      observer.observe(ref.current);
  
      return () => {
        observer.disconnect();
      };
    }, [ref, observer]);
  
    return isIntersecting;
  }

export default MoviesTable
