import { createContext, useContext, useState } from "react";

export interface IProviderProps {
    children?: any;
}

type YearContextType = {
    context: string | number
}

const defaultValue:{state:string} = {
   state: "test",
   setState: (state: YearContextType) => { }
}
export const YearContext = createContext(defaultValue)

export const AppProvider = (props: IProviderProps) => {
    const [state, setState] = useState<string|number>(defaultValue.state);

    return (
        <YearContext.Provider value={{ state, setState }}>
            {props.children}
        </YearContext.Provider>
    );
};
