import React, {useEffect, useState, useRef, useMemo, Ref} from 'react';
import axios from "axios";
import {Table, Container} from 'react-bootstrap';
import { MovieDto } from '../model';
import { keyboard } from '@testing-library/user-event/dist/keyboard';
import { Waypoint } from 'react-waypoint';
import InfiniteScroll from './InfiniteScroll';
import { BASE_API } from '../constants';

const MoviesTable: React.FC = () => {
    const [movies, setMovies] = useState<MovieDto[]>([])
    const [page, setPage] = useState<number>(0)
    const [size, setSize] = useState<number>(10)
    const [loading, setLoading] = useState(false);
    const [last, setLast] = useState(false)

    const hasMoreData = !last;

    const loadMoreNumbers = () => {
        setLoading(true);
            axios.get(BASE_API, {params: {"pageSize" : size, "pageNumber": page}}).then(res =>{
                setPage(page+1)
                setMovies([...movies, ...res.data.content]);
                setLoading(false);
              })
    };

  return (
    <Container className='tableFixHead'>
        <InfiniteScroll
            hasMoreData={hasMoreData}
            isLoading={loading}
            onBottomHit={loadMoreNumbers}
            loadOnMount={true}
        >
            <Table>
                <thead>
                <tr>
                    <th>Rank</th>
                    <th>Title</th>
                    <th>Date</th>
                    <th>Rating</th>
                    <th>Revenue</th>
                </tr>
                </thead>
                <tbody>
                {movies.map((movie, index) =>(
                    <tr key={index}>
                    <th>{index+1}</th>
                    <th>{movie.title}</th>
                    <th>{new Date(movie.date).getFullYear}</th>
                    <th>{movie.rating}</th>
                    <th>${movie.revenue}</th>
                    </tr>
                )
                )}
                </tbody>
            </Table>
        </InfiniteScroll>
      </Container>
  )
}

export default MoviesTable
