import React, {useEffect, useState, useRef} from 'react';
import axios from "axios";
import {Table, Container} from 'react-bootstrap';
import { MovieDto } from '../model';
import { keyboard } from '@testing-library/user-event/dist/keyboard';
import { Waypoint } from 'react-waypoint';

const MoviesTable: React.FC = () => {
    const [movies, setmovies] = useState<MovieDto[]>([])
    const [page, setpage] = useState<number>(0)
    const [size, setsize] = useState<number>(40)

    const URL: string = "http://localhost:8080/app/movies";
  
    const handleScroll = () =>{
        console.log("handle1")
        console.log(movies)
        axios.get(URL, {params: {"pageSize" : size, "pageNumber": page}}).then(res =>{
            setpage(page+1)
            setmovies([...movies, ...res.data.content]);
          })
    }

    useEffect(() => {
      axios.get(URL, {params: {"pageSize" : size, "pageNumber": page}}).then(res =>{
        setpage(page+1)
        setmovies([...movies, ...res.data.content]);
        console.log("effect")
        console.log(movies)
      })
    }, [])

  return (
    <Container className='tableFixHead' onScroll={handleScroll}>
    <Table striped bordered hover variant='dark' >
        <thead>
          <tr>
            <th>Rank</th>
            <th>Title</th>
            <th>Date</th>
            <th>Rating</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie, index) =>(
            <tr key={index}>
              <th>{index+1}</th>
              <th>{movie.title}</th>
              <th>{movie.date}</th>
              <th>{movie.rating}</th>
              <th>{movie.revenue}</th>
            </tr>
          )
          )}
          <Waypoint onEnter={handleScroll} />
        </tbody>
      </Table>
      </Container>
  )
}

export default MoviesTable
