import React, {useEffect, useState, useRef} from 'react';
import axios from "axios";
import {Table, Container} from 'react-bootstrap';
import { MovieDto } from '../model';
import { keyboard } from '@testing-library/user-event/dist/keyboard';

const MoviesTable: React.FC = () => {
    const ref = useRef()
    const [movies, setmovies] = useState<MovieDto[]>([])
    const URL: string = "http://localhost:8080/app/movies";
  
    const handleScroll = () =>

    useEffect(() => {
        const div:any = ref.current
        div.addEventListener("scroll", handleScroll)
      axios.get(URL, {params: {"pageSize" : 50}}).then(res =>{
        setmovies(res.data.content);
      })
    }, [])

  return (
    <Container className='tableFixHead'>
    <Table striped bordered hover variant='dark' >
        <thead>
          <tr>
            <th>Rank</th>
            <th>Title</th>
            <th>Date</th>
            <th>Rating</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie, index) =>(
            <tr key={index}>
              <th>{index+1}</th>
              <th>{movie.title}</th>
              <th>{movie.date}</th>
              <th>{movie.rating}</th>
              <th>{movie.revenue}</th>
            </tr>
          )
          )}
        </tbody>
      </Table>
      </Container>
  )
}

export default MoviesTable
